import React, {Component} from 'react';
import './App.css';
import axios from "axios"
import UserCard from "./Components/UserCard"


export default class App extends Component {
  state = {
    user: [],
    follower: []
  }


  //fetching User Data from api
  componentDidMount(){
    axios.get('https://api.github.com/users/aliciapetgrave')
    .then(response => {
      console.log(response.data)
      this.setState({
        user: response.data
      })
    })
    .catch(error => {
      console.log(error)
    })

    //fetching user followers data
    axios.get('https://api.github.com/users/aliciapetgrave/followers')
    .then(response => {
      console.log(response.data)
      this.setState({
        follower: response.data
      })
    })
    .catch(error => {
      console.log(error)
    })
  }





  render() {
    return (
      <div className="App">
        <h1>GitHub UserCard</h1>
        <UserCard 
        name={this.state.user.name} 
        location={this.state.user.location} 
        avatar={this.state.user.avatar_url} 
        followers={this.state.user.followers} 
        following={this.state.user.following}/>

        {this.state.follower.map(person => {
          return (
            <div className="followerscard">
              <p>Name: {person.login}</p>
              <img src={person.avatar_url}/>
              <p>Location: {person.location}</p>
            </div>  
          )
        })}
      </div>
    )
  }
}
